  if not product_name:
      return "", ""

  for keyword, b2m in mapping["product2brand2manufacturer"].items():
    if keyword in product_name:
      brand = next(iter(b2m.keys()))
      manufacturer = b2m[brand]
      return brand, manufacturer

  return "", ""

